#!/bin/bash

while [ ! -f /home/nobody/vpn_external_ip.txt ]
do
	sleep 0.1s
done

# get vpn external ip address (file contents generated by /root/getvpnextip.sh)
external_ip=$(</home/nobody/vpn_external_ip.txt)

if [[ "${external_ip}" == "0.0.0.0" ]]; then
	echo "[warn] Cannot determine external IP address, possible connection issues at present."
else
	if [[ "${DEBUG}" == "true" ]]; then
		echo "[debug] External IP address from tunnel is '${external_ip}'"
	fi
fi
